import { NgModule } from '@angular/core';
import { RouterModule, Routes } from '@angular/router';
import { ProductFormComponent } from './components/product-form/product-form.component';
import { ProductListComponent } from './components/product-list/product-list.component';
import { AuthGuard } from './guards/auth.guard';
import { LoginComponent } from './components/login/login.component';

const routes: Routes = [
  { path: '', redirectTo: '/login', pathMatch: 'full' }, // Redirige al login si la ruta está vacía
  { path: 'products', component: ProductListComponent, canActivate: [AuthGuard] },  // Solo se accede si está autenticado
  { path: 'add-product', component: ProductFormComponent, canActivate: [AuthGuard] },  // Solo se accede si está autenticado
  { path: 'login', component: LoginComponent },  // Ruta para el login
];

@NgModule({
  imports: [RouterModule.forRoot(routes)],
  exports: [RouterModule]
})
export class AppRoutingModule { }
